// fetch dishes from recipes.json
// and display them in a list
// with a button to add them to the shopping list

export function Dishes() {
  fetchedDishes = () => {
    fetch("/recipes.json")
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          dishes: data,
        });
      });
  };

  return (
    <div>
      <h1>Dishes</h1>
      <p>fetching dishes...</p>
      {this.state.dishes.map((dish) => (
        <div key={dish.id}>
          <h2>{dish.name}</h2>
          <p>{dish.description}</p>
          <button>Add to shopping list</button>
        </div>
      ))}
    </div>
  );
}
