import recipes from "../data/recipes.json";
import React, { useState } from "react";
import Card from "react-bootstrap/Card";

// function that maps out all of the recipes from the data

export function Recipes() {
  return (
    <div className="dishCardsContainer">
      {recipes.map((recipe) => {
        return (
          <div key={recipe.id}>
            <Card className="dishCard">
              <Card.Body>
                <Card.Title className="dishCardTitle">{recipe.name}</Card.Title>
                <Card.Text>
                  <i>{recipe.ingredients}</i>
                </Card.Text>
                <Card.Button
                  className="dishCardLink"
                  href={recipe.link}
                  target="_blank"
                >
                  ({recipe.link} != null ?
                  <button>
                    <a href={recipe.link}>Se opskrift</a>
                  </button>{" "}
                  : "")
                </Card.Button>
              </Card.Body>
            </Card>
          </div>
        );
      })}
    </div>
  );
}
