import recipes from "../data/recipes.json";
import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";

// function that maps out all of the recipes from the data

export function Recipes() {
  return (
    <div className="dishCardsContainer">
      {recipes.map((recipe) => {
        return (
          <div key={recipe.id}>
            <Card className="dishCard">
              <Card.Body>
                <Card.Title className="dishCardTitle">{recipe.name}</Card.Title>
                <Card.Text>
                  <i>{recipe.ingredients}</i>
                </Card.Text>
                <div className="dishCardLink">
                  {recipe.link != "" ? (
                    <Button href={recipe.link} target="_blank">
                      {" "}
                      Se opskrift{" "}
                    </Button>
                  ) : (
                    ""
                  )}
                </div>
              </Card.Body>
            </Card>
          </div>
        );
      })}
    </div>
  );
}
