import recipes from "../data/recipes.json";
import React, { useState } from "react";

import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";

// function that maps out all of the recipes from the data
export function Recipes() {
  return (
    <div className="dishCards">
      {recipes.map((recipe) => {
        return (
          <div className="dish" key={recipe.id}>
            <Card style={{ width: "18rem" }}>
              <Card.Body>
                <Card.Title>{recipe.name}</Card.Title>
                <Card.Text>Ingredienser: {recipe.ingredients}</Card.Text>
                <Button variant="primary">
                  Se opskrift
                  <Card.Link href={recipe.link} target="_blank"></Card.Link>
                </Button>
              </Card.Body>
            </Card>
          </div>
        );
      })}
    </div>
  );
}
