import recipes from "../data/recipes.json";
import React from "react";
import Card from "react-bootstrap/Card";

const menuItems = [...new Set(recipes.map((recipe) => recipe.ingredients))];

const Buttons = ({ setItem, menuItems }) => {
  return (
    <>
      <div className="d-flex justify-content-center">
        {menuItems.map((Val, id) => {
          return (
            <button
              className="btn-dark text-white p-1 px-2 mx-5 btn fw-bold"
              onClick={() => filterItem(Val)}
              key={id}
            >
              {Val}
            </button>
          );
        })}
        <button
          className="btn-dark text-white p-1 px-3 mx-5 fw-bold btn"
          onClick={() => setItem(Data)}
        >
          All
        </button>
      </div>
    </>
  );
};

export default Buttons;

const filterItem = (curcat) => {
  const newItem = Data.filter((newVal) => {
    return newVal.category === curcat;
    // comparing category for displaying data
  });
  setItem(newItem);
};

// function that maps out all of the recipes from the data

export function Recipes() {
  return (
    <div className="dishCardContainer">
      {recipes.map((recipe) => {
        return (
          <div className="dishCard" key={recipe.id}>
            <Card style={{ width: "18rem" }}>
              <Card.Body>
                <Card.Title className="dishCardTitle">{recipe.name}</Card.Title>
                <Card.Text>
                  <i>{recipe.ingredients}</i>
                </Card.Text>
                <Card.Link href={recipe.link} target="_blank">
                  {recipe.link}
                </Card.Link>
              </Card.Body>
            </Card>
          </div>
        );
      })}
    </div>
  );
}
